  };

  const renderCheckpointOverview = (checkpoint) => {
    if (checkpoint.id === 'month_3') {
      return (
        <View style={styles.overviewSection}>
          <Text style={styles.overviewTitle}>‰∏ªË¶ÅË©ï‰æ°È†ÖÁõÆ:</Text>
