  };

  return (
    <ScrollView style={styles.container}>
      {/* „Éò„ÉÉ„ÉÄ„Éº */}
      <Card style={styles.headerCard}>
        <Card.Content>
          <Text style={styles.screenTitle}>Ë©ï‰æ°„ÉÅ„Çß„ÉÉ„ÇØ„Éù„Ç§„É≥„Éà</Text>
          <Text style={styles.screenDescription}>
            ACL„É™„Éè„Éì„É™„ÉÜ„Éº„Ç∑„Éß„É≥„ÅÆÈáçË¶Å„Å™Ë©ï‰æ°ÊôÇÊúü„Å´„Åä„Åë„ÇãÂøÖÈ†à„ÉÅ„Çß„ÉÉ„ÇØÈ†ÖÁõÆ„Åß„Åô„ÄÇ
            ÂêÑÊÆµÈöé„ÅßÈÅ©Âàá„Å™Ë©ï‰æ°„ÇíË°å„ÅÑ„ÄÅÂÆâÂÖ®„Å™ÂõûÂæ©„Å®Á´∂ÊäÄÂæ©Â∏∞„ÇíÁõÆÊåá„Åó„Åæ„Åó„Çá„ÅÜ„ÄÇ
          </Text>
        </Card.Content>
      </Card>

      {/* „Çø„Ç§„É†„É©„Ç§„É≥Ê¶ÇË¶Å */}
      <Card style={styles.timelineCard}>
        <Card.Content>
          <Text style={styles.timelineTitle}>Ë©ï‰æ°„Çø„Ç§„É†„É©„Ç§„É≥</Text>
