            <View style={styles.timelineItem}>
              <View style={[styles.timelineMarker, { backgroundColor: '#FF9800' }]} />
              <View style={styles.timelineContent}>
                <Text style={styles.timelineItemTitle}>Ë°ìÂæå3„É∂Êúà</Text>
                <Text style={styles.timelineItemDesc}>„É©„É≥„Éã„É≥„Ç∞ÈñãÂßãÂü∫Ê∫ñË©ï‰æ°</Text>
              </View>
            </View>
            <View style={styles.timelineLine} />
            <View style={styles.timelineItem}>
              <View style={[styles.timelineMarker, { backgroundColor: '#4CAF50' }]} />
              <View style={styles.timelineContent}>
                <Text style={styles.timelineItemTitle}>Ë°ìÂæå6„É∂Êúà‰ª•Èôç</Text>
                <Text style={styles.timelineItemDesc}>Á´∂ÊäÄÂæ©Â∏∞„ÉÅ„Çß„ÉÉ„ÇØ„É™„Çπ„Éà</Text>
              </View>
            </View>
          </View>
        </Card.Content>
      </Card>

      {/* Ë©ï‰æ°„ÉÅ„Çß„ÉÉ„ÇØ„Éù„Ç§„É≥„Éà */}
      <Text style={styles.sectionTitle}>Ë©ï‰æ°„ÉÅ„Çß„ÉÉ„ÇØ„Éù„Ç§„É≥„Éà</Text>
      {evaluationCheckpoints.map(renderCheckpointCard)}

      {/* Ë©≥Á¥∞„É¢„Éº„ÉÄ„É´ */}
      <Portal>
        <Modal
          visible={showDetailModal}
          onDismiss={() => setShowDetailModal(false)}
