                <Button
                  mode="contained"
                  onPress={() => {
                    setShowDetailModal(false);
                    handleCheckpointPress(selectedCheckpoint);
                  }}
                  style={[styles.modalButton, { backgroundColor: selectedCheckpoint.color }]}
                >
                  Ë©ï‰æ°„ÇíÈñãÂßã„Åô„Çã
                </Button>
              </View>
            </ScrollView>
          )}
